       function getTx(data){
            web3.eth.getTransaction(data,function(error, result) {
                if(error != null)
                    console.log("Couldn't get accounts");
                if(result.blockNumber==null)
                    {getTx(result.hash);}
                if(result.blockNumber!=null)
                    return result;
            }); 
        }

      
